import { 
    RECEIVE_POSTS, 
    ADD_POST,
    UPVOTE_POST,
    DELETE_POST,
    UPDATE_POST,
    DOWNVOTE_POST } from "../actions";

import { api_votePost, api_deletePost, api_updatePost, api_addPost, api_getPostByCategory } from '../API-calls'

export default (state = [], action) => {

    const { posts, type, post, id, title, body, category } = action;

    switch (type) {

        // UPDATE POSTS ===========================================

        case RECEIVE_POSTS:
            return [ ...posts ];

        case ADD_POST:
            if(api_addPost(post)){
                return [...state, post]
            }else {
                return state
            }


        // VOTE POST ===============================================

        case DOWNVOTE_POST:

            if(api_votePost(id, 'downVote')){
                return state.map(post => {
                    if( post.id === id ) post.voteScore--
                    return post
                })
            }else{
                return state
            }


        case UPVOTE_POST:

            if(api_votePost(id, 'upVote')){
                return state.map(post => {
                    if( post.id === id ) post.voteScore++
                    return post
                })
            }else{
                return state
            }


        // EDIT POST ================================================

        case DELETE_POST:

            if(api_deletePost(id)){
                return state.map(post => {
                    if(post.id === id){
                        post.deleted = true
                    }
                    return post
                })
            }else{
                return state
            }


        case UPDATE_POST:

            if(api_updatePost(id, title, body)){
                return state.map(post => {
                    if(post.id === id){
                        post.body = body;
                        post.title = title;
                    }
                    return post
                })
            }

            return state

        default:
            return state;
    }
  };
