import React, { useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { api_votePost } from '../API-calls'
import CommentForm from './CommentForm'

export default function FullPost({ match }) {

    const id = match.params.id

    useEffect(() => {

        getSinglePost()

    }, [])

    const getSinglePost = async () => {

        const post = await api_getSinglePost(id)
        setPost(dispatch(({type: ADD_POST, post})).post)

    }

    const getComments = async () => {
        const comments = await api_getComments(id)

        setComments(dispatch(({type: RECEIVE_COMMENTS, comments})).comments)
    }

    return (
        <div>
            {post.deleted === false ?

            <li>

                <h3>{post.title}</h3>

                <p>{post.body}</p>

                <Vote handleClick={handleClick} score={post.voteScore} />

                <button>delete</button>
                <button>edit</button>


                    {comments.map(comment => {
                        if(!comment.deleted) return <Comment 
                                                        key={comment.id} 
                                                        comment={comment}/>
                    })}

                    <CommentForm parentId={id || match.params.id}/>

            </li>
        :
        ''
        }

        </div>
    )
}
