import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { 
    api_getSinglePost, 
    api_getComments, 
    api_votePost } from '../API-calls'

import { 
    UPVOTE_POST,
    DOWNVOTE_POST,
    DELETE_POST,
    UPDATE_POST } from '../actions'


import Vote from './Vote'
import Comment from './Comment'
import CommentForm from './CommentForm'

export default function Post({ match, id  }) {

    const [post, setPost] = useState([])
    const [comments, setComments] = useState(false)

    useEffect(() => {

        getSinglePost()
        if(match) getComments()

    }, [])

    const handleClick = async (action) => {
        switch (action){

            case 'downVote':
                if(api_votePost(id || match.params.id, action)) dispatch({type: DOWNVOTE_POST, id: post.id})
                break

            case 'upVote':
                if(api_votePost(id || match.params.id, action)) dispatch({type: UPVOTE_POST, id: post.id})
                break

            case 'delete':
                if(api_deletePost(url, post.id)) dispatch({type: DELETE_POST, id: post.id})
                break

            default:
                return
        }
    }


    const getSinglePost = async () => {
        const post = await api_getSinglePost(id || match.params.id)
        setPost(post)
    }
    const getComments = async () => {
        const comments = await api_getComments(match.params.id)
        setComments(comments)
    }


    return (
        <>
            {post.deleted === false ?

                <li>

                    <h3>{post.title}</h3>

                    <p>{post.body}</p>

                    <Vote score={post.voteScore} />

                    <button>delete</button>
                    <button>edit</button>

                    {comments ?
                        <>
                            {comments.map(comment => {
                                if(!comment.deleted) return <Comment 
                                                                key={comment.id} 
                                                                comment={comment}/>
                            })}

                            <CommentForm parentId={id || match.params.id}/>
                        </>
                        :
                        ''
                    }
                </li>
            :
            ''
            }
        </>

    )
}
