import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { api_getSinglePost, api_getComments } from '../API-calls'

import Vote from './Vote'
import Comment from './Comment'

export default function Post({ match, id  }) {

    const [post, setPost] = useState([])
    const [comments, setComments] = useState(false)

    useEffect(() => {

        getSinglePost()
        if(match) getComments()

    }, [])


    const getSinglePost = async () => {
        const post = await api_getSinglePost(id || match.params.id)
        setPost(post)
    }
    const getComments = async () => {
        const comments = await api_getComments(match.params.id)
        setComments(comments)
    }


    return (
        <>
            {post.deleted === false ?

                <li>

                    <h3>{post.title}</h3>

                    <p>{post.body}</p>

                    <Vote score={post.voteScore} />

                    <button>delete</button>
                    <button>edit</button>

                    {comments ?

                        comments.map(comment => {
                            if(!comment.deleted) return <Comment 
                                                            parentId={match.params.id}
                                                            key={comment.id} 
                                                            comment={comment}/>
                        })

                        :

                        ''

                    }

                </li>
            :
            ''
            }
        </>

    )
}
