import  { 
    RECEIVE_COMMENTS,
    ADD_COMMENT,
    DOWNVOTE_COMMENT,
    DELETE_COMMENT,
    UPDATE_COMMENT,
    UPVOTE_COMMENT } from "../actions";

import { api_voteComment } from '../API-calls'


export default (state = [], action) => {

    const { comments, comment, type, id } = action;

    switch(type) {

        // UPDATE COMMENTS ===========================================

        case RECEIVE_COMMENTS:

            return [ ...comments]

        case ADD_COMMENT:

            return [...state, comment]


        // VOTE COMMENT ===============================================

        case DOWNVOTE_COMMENT:
            if(api_voteComment(id, 'downVote')){
                return state.map(comment => {
                    if( comment.id === id ) comment.voteScore--
                    return comment
                })
            }else {
                return state
            }



        case UPVOTE_COMMENT:

            return state.map(comment => {
                if( comment.id === id ) comment.voteScore++
                return comment
            })

        // EDIT COMMENT ================================================

        case DELETE_COMMENT:

            return state.filter(comment => comment.id !== id)

        case UPDATE_COMMENT:

            return state

        default:
            return state
    }

  }
