import React, {useEffect, useState} from 'react'
import { Link } from 'react-router-dom'
import { api_getAllPosts } from '../API-calls'
import { useSelector, useDispatch } from 'react-redux'
import { RECEIVE_POSTS } from '../actions'

import Post from './Post'

export default function PostsTeasers({handleClick}) {

    const dispatch = useDispatch()
    const posts = useSelector(state => state.posts)

    useEffect(() => {

        getAllPosts()

    }, [])

    const getAllPosts = async () => {
        const posts = await api_getAllPosts()
        dispatch({type: RECEIVE_POSTS, posts: posts})

    }


    return (
        <ul className="posts-container">

            {posts.map(post => 


                <Post handleClick={handleClick} key={post.id} post={post} id={post.id} />

            )}
        </ul>
    )
}
