import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { api_getSinglePost } from '../API-calls'

import Vote from './Vote'

export default function Post({ match, id  }) {

    const [post, setPost] = useState([])

    useEffect(() => {

        getSinglePost()
        if(match.isExact) console.log('bam')
    }, [])


    const getSinglePost = async () => {
        const post = await api_getSinglePost(id || match.params.id)
        setPost(post)
    }


    return (
        <li>

            <h3>{post.title}</h3>

            <p>{post.body}</p>

            <Vote score={post.voteScore} />

            <button>delete</button>
            <button>edit</button>

        </li>

    )
}
