import { 
    RECEIVE_POSTS, 
    ADD_POST,
    UPVOTE_POST,
    DELETE_POST,
    UPDATE_POST,
    DOWNVOTE_POST } from "../actions";

import { api_votePost } from '../API-calls'

export default (state = [], action) => {

    const { posts, type, post, id } = action;

    switch (type) {

        // UPDATE POSTS ===========================================

        case RECEIVE_POSTS:
            return [ ...posts ];

        case ADD_POST:
            return [...state, post ]

        // VOTE POST ===============================================

        case DOWNVOTE_POST:

            return state.map(post => {
                if( post.id === id ) post.voteScore--
                return post
            })

        case UPVOTE_POST:

            return state.map(post => {
                if( post.id === id ) post.voteScore++
                return post
            })

        // EDIT POST ================================================

        case DELETE_POST:

            return state.filter(post => post.id !== id)

        case UPDATE_POST:

        return state

        default:
            return state;
    }
  };
