import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { api_votePost, api_getSinglePost } from '../API-calls'
import { ADD_POST, 
    UPVOTE_POST,
    DOWNVOTE_POST,
    DELETE_POST,
    UPDATE_POST } from '../actions'
import CommentForm from './CommentForm'
import Vote from './Vote'

export default function FullPost( { match } ) {

    const dispatch = useDispatch()
    const id = match.params.id
    const posts = useSelector(state => state.posts)
    const [post, setPost] = useState([])
    const [title, setTitle] = useState(post.title)
    const [body, setBody] = useState(post.body)
    const [edit, setEdit] = useState(false)

    useEffect(() => {

        // if direct link - get single post, else use store
        if(posts.length > 0){
            posts.forEach(post => {
                if(post.id === id) setPost(post)
            });
        }else{
            getSinglePost()
        }
    }, [])

    const getSinglePost = async () => {
        const post = await api_getSinglePost(id)
        dispatch({type: ADD_POST, post })
        setPost(post)
    }
    const handleClick = (action) => {

        switch(action){
            case 'upVote':
                dispatch({type: UPVOTE_POST, id})
                break

            case 'downVote':
                dispatch({type: DOWNVOTE_POST, id})
                break

            case 'delete':
                dispatch({type: DELETE_POST, id})
                break

            case 'edit-save':
                dispatch({type: UPDATE_POST, id, title, body})
                setEdit(false)
            default:
                break
        }
    }

    return (
        <div>
            {post.deleted === false ?

            <li>

                {edit?
                    <>
                        <input 
                            onChange={e => setBody(e.target.value)}
                            value={body} 
                            placeholder='post' />
                        <input 
                            onChange={e => setTitle(e.target.value)}
                            value={title} 
                            placeholder='title' />
                        <button
                            onClick={() => handleClick('edit-save')}
                        >save</button>
                    </>
                    :
                    <>
                        <h3>{post.title}</h3>
                        <p>{post.body}</p>
                    </>
                }


                <Vote handleClick={handleClick} score={post.voteScore} />

                <button>delete</button>
                <button onClick={() => setEdit(true)}>edit</button>


                    {/* {comments.map(comment => {
                        if(!comment.deleted) return <Comment 
                                                            key={comment.id} 
                                                            comment={comment}/>
                        })}

                    <CommentForm parentId={id || match.params.id}/> */}

            </li>
            :
            ''
            }
        </div>
    )
}
