import { RECEIVE_POSTS } from "../actions";
import { combineReducers } from "redux";

const posts = (state = [], action) => {
  const { posts } = action;
  switch (action.type) {
    case RECEIVE_POSTS:
      return [
        ...state,
        ...posts
      ];
    default:
      return state;
  }
};


export default combineReducers({ posts });
